mapper.select:
SELECT
user_id userId,id_number idNumber,real_name realName,bank_name bankName,create_time createTime,`status` status,phone phone
FROM createcardinfo where phone =#{phone}

SELECT c.id id,c.user_id userId,c.id_number idNumber,c.real_name realName,b.bank_name bankName,c.create_time createTime,c.`status` status,c.phone phone,b.superior_bonus superiorBonus,b.bank_bonus bankBonus,c.update_time updateTime FROM createcardinfo c,bank b WHERE c.bank_name = b.bank_id
and c.phone like '%${phone}%'
and c.status = #{status}
